mapper.select:
		select
		
			distinct
    
		
			 ${column} 		
			
				
			
		
		from tb_user
		${join}
		
			
		
		 group by ${groupBy}    
		 having ${having}    
		
			order by ${orderByClause}

		select
		
		from tb_user
		where 1 = 1
		 and
			user_name = #{userName,jdbcType=VARCHAR}
      
		 and
			password = #{password,jdbcType=VARCHAR}
      
		 and
			country = #{country,jdbcType=INTEGER}
      
		 and
			expired_date = #{expiredDate,jdbcType=TIMESTAMP}
      
		 and
			credentials_expired =
			#{credentialsExpired,jdbcType=VARCHAR}
      
		 and
			full_name = #{fullName,jdbcType=VARCHAR}
      
		 and
			org = #{org,jdbcType=INTEGER}
      
		 and
			gender = #{gender,jdbcType=VARCHAR}
      
		 and
			age = #{age,jdbcType=INTEGER}
      
		 and
			address = #{address,jdbcType=VARCHAR}
      
		 and
			phone = #{phone,jdbcType=VARCHAR}
      
		 and
			mobile = #{mobile,jdbcType=VARCHAR}
      
		 and
			ssn = #{ssn,jdbcType=VARCHAR}
      
		 and
			mail = #{mail,jdbcType=VARCHAR}
      
		 and
			user_type = #{userType,jdbcType=INTEGER}
      
		 and
			notify_mode = #{notifyMode,jdbcType=INTEGER}
      
		 and
			description = #{description,jdbcType=VARCHAR}
      
		 and
			creator = #{creator,jdbcType=INTEGER}
      
		 and
			status = #{status,jdbcType=VARCHAR}
      
		 and
			creation_time = #{creationTime,jdbcType=TIMESTAMP}
		
		 and
			retention = #{retention,jdbcType=VARCHAR}
      
		 and
			ext = #{ext,jdbcType=VARCHAR}

select
 distinct    

	 ${column} 		
	
		
	

from tb_user
${join}

	

 group by ${groupBy}    
 having ${having}    
 order by ${orderByClause}
