mapper.select:
select

  distinct

		 ${column} 			
from  tb_video
 ${join}
  

	 group by ${groupBy}     having ${having}    
  order by ${orderByClause}

select     from tb_video
     where 1 = 1 
      and 
       app_name = #{appName,jdbcType=VARCHAR}
     
      and 
       name = #{name,jdbcType=VARCHAR}
     
      and 
       title = #{title,jdbcType=VARCHAR}
     
      and 
       play_address = #{playAddress,jdbcType=VARCHAR}
     
      and 
       thumbnail_address = #{thumbnailAddress,jdbcType=VARCHAR}
     
      and 
       size = #{size,jdbcType=INTEGER}
     
      and 
       subtitles = #{subtitles,jdbcType=VARCHAR}
     
      and 
       recording_time = #{recordingTime,jdbcType=TIMESTAMP}
     
      and 
       labels = #{labels,jdbcType=VARCHAR}
     
      and 
       speaker = #{speaker,jdbcType=VARCHAR}
     
      and 
       organization = #{organization,jdbcType=VARCHAR}
     
      and 
       description = #{description,jdbcType=VARCHAR}
     
      and 
       upload_user_id = #{uploadUserId,jdbcType=INTEGER}
     
      and 
       play_count = #{playCount,jdbcType=INTEGER}
     
      and 
       comments = #{comments,jdbcType=INTEGER}
     
      and 
       is_comment = #{isComment,jdbcType=INTEGER}
     
      and 
       is_hot = #{isHot,jdbcType=INTEGER}
     
      and 
       is_download = #{isDownload,jdbcType=INTEGER}
     
      and 
       language = #{language,jdbcType=VARCHAR}
     
      and 
       bit_rate = #{bitRate,jdbcType=INTEGER}
     
      and 
       copyright = #{copyright,jdbcType=VARCHAR}
     
      and 
       password = #{password,jdbcType=VARCHAR}
     
      and 
       status = #{status,jdbcType=VARCHAR}
     
      and 
       problem = #{problem,jdbcType=VARCHAR}
     
      and 
       answer = #{answer,jdbcType=VARCHAR}
     
      and 
       protect_type = #{protectType,jdbcType=VARCHAR}
     
      and 
       creation_time = #{creationTime,jdbcType=TIMESTAMP}
     
      and 
       retention = #{retention,jdbcType=VARCHAR}
     
      and 
       ext = #{ext,jdbcType=VARCHAR}
